sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.treeNumber = 0;
	data.isAdmin = false;
	data.needCompensation = true;

	if (gs.hasRole("admin")){
		data.isAdmin= true;
	}

	var gr = new GlideRecord('x_snc_tree4meeting_compensazione');
	gr.query();

	if(gr.next()){
		data.needCompensation = false;
	}

	var agg = new GlideAggregate('x_snc_tree4meeting_st_impatto_ambientale_eventi');
	agg.addAggregate("SUM", "alberi_necessari");
	//agg.addQuery("nome_evento='Evento Reskilling del personale'");
	agg.setGroup(false);
	agg.query();

	while (agg.next()) {
			var alberi_necessari = agg.getAggregate('SUM', 'alberi_necessari');
			data.treeNumber = Number(alberi_necessari);
	}
})();
