sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */
	var c = this;



	google.charts.load("current", {packages:["corechart"]});
	google.charts.setOnLoadCallback(drawChart);
	function drawChart() {
		var data = google.visualization.arrayToDataTable([
			['Task', 'Vaccination Status'],
			['Vaccinated',    54],
			['Scheduled',     28],
			['Not scheduled',  18],
			['Did not disclosed',  17]
		]);

		var options = {
			'width': 200,
			'height': 200,
			'chartArea': {'width': '90%', 'height': '90%'},
			'legend': {'position': 'none'},
			'pieSliceText': 'none',
			colors: [c.data.success,c.data.warning,c.data.danger,c.data.info]

		};

		var chart = new google.visualization.PieChart(document.getElementById('donutchart-vaccination-status'));
		chart.draw(data, options);
	}



};

