sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
	//Type appropriate comment here, and begin script below
	var eventSysId = '';
	var eventTable = new GlideRecord('x_snc_tree4meeting_event');
	if(eventTable.get(input.sys_id)){
		eventSysId = input.sys_id;
	}else{
		var attendeeGr1 = new GlideRecord('x_snc_tree4meeting_attendee');
		if(attendeeGr1.get(input.sys_id)){
			eventSysId = attendeeGr1.event_name.sys_id;
		}
	}
	if(eventSysId){
		var attendeeGr = new GlideRecord('x_snc_tree4meeting_attendee');
		attendeeGr.addQuery('attendee_status', 'waitlisted');
		attendeeGr.addQuery('attendee_name', gs.getUserID());
		attendeeGr.addQuery('event_name', eventSysId);
		attendeeGr.query();
		if (attendeeGr.next()) {
			attendeeGr.attendee_status = 'cancelled_waitlist';
			attendeeGr.update();
		}
	}

})(input);
